// export default comments;

import { createSlice } from "@reduxjs/toolkit";

const initialState = [];

const replySlice = createSlice({
  name: "replies",
  initialState,
  reducers: {
    getReplies(state, action) {
      return action.payload.reduce((acc, comm) => {
        const { replies } = comm;
        return acc.concat(replies);
      }, []);
    },
    addReplies(state, action) {
      state.push(action.payload);
    },
  },
});

export const { getReplies, addReplies } = replySlice.actions;
export default replySlice.reducer;
