const replies = (state = [], action) => {
  switch (action.type) {
    case "COMMENTS_RECEIVED": {
      const replies = action.comments.reduce((acc, comm) => {
        const [replies] = comm;
        return acc.concat(replies);
      }, []);
      return replies;
    }
    default: {
      return state;
    }
  }
};

export default replies;
