const comments = (state = [], action) => {
  switch (action.type) {
    case "COMMENTS_RECEIVED": {
      const commentsWithoutReplies = action.comments.reduce((acc, comm) => {
        const [replies, ...commentWithoutReplies] = comm;
        return acc.concat(commentWithoutReplies);
      }, []);
      return commentsWithoutReplies;
    }
    default: {
      return state;
    }
    case "COMMENT_ADDED": {
      return state.concat(action.newComment);
    }
  }
};

export default comments;
